import Navbar from "../../components/navbar/Navbar"
import Header from "../../components/header/Header"
import "./hotel.css"
import { faCircleArrowLeft, faCircleArrowRight, faCircleXmark, faLocationDot } from "@fortawesome/free-solid-svg-icons"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import MailList from '../../components/mailList/MailList'
import Footer from '../../components/footer/Footer'
import { useState } from "react";


const Hotel = () => {
  const [slideNumber,setSlideNumber] = useState(0);
  const [open,setOpen] = useState(false);
  const photos = [
    {
      src:"data:image/jpeg;base64,"
    },
    {
      src:"data:image/jpeg;base64,"
    },
    {
      src:"data:image/jpeg;base64,"
    }
  ]

  const handleOpen =(i) => {
    setSlideNumber(i);
    setOpen(true);
  }

  const handleMove =(direction) => {
    let newSlideNumber;
    if(direction==="l"){
      newSlideNumber = slideNumber === 0 ? 2 : slideNumber - 1;
    }
    else{
      newSlideNumber = slideNumber === 2 ? 0 : slideNumber+1;
    }
    setSlideNumber(newSlideNumber);
  }
  return (
    <div>
        <Navbar/>
        <Header type="list"/>
        <div className="hotelContainer">
          {open && <div className="slider">
            <FontAwesomeIcon icon={faCircleXmark} className="close" onClick={()=>setOpen(false)}/>
            <FontAwesomeIcon icon={faCircleArrowLeft} className="arrow" onClick={()=>handleMove("l")}/>
            <div className="sliderWrapper">
              <img src={photos[slideNumber].src} alt="" className="sliderImg" />
            </div>
            <FontAwesomeIcon icon={faCircleArrowRight} className="arrow" onClick={()=>handleMove("r")}/>
          </div>}
          <div className="hotelWrapper">
            <button className="bookNow">Book Now!</button>
            <h1 className="hotelTitle">The Times Square Hotel</h1>
            <div className="hotelAddress">
              <FontAwesomeIcon icon={faLocationDot}/>
              <span>Manhattan, New York, 10036</span>
            </div>
            <span className="hotelDistance">100m from Times Square</span>
            <span className="hotelPriceHighlight">Book now for $250!</span>
            <div className="hotelImages">
              {photos.map((photo,i)=>(
                <div className="hotelImgWrapper">
                  <img onClick={()=>handleOpen(i)} src={photo.src} alt="" className="hotelImg" />
                </div>
              ))}
            </div>
            <div className="hotelDetails"> 
              <div className="hotelDetailsTexts">
                <h1 className="hotelTitle">Stay in the heart of New York</h1>
                <p className="hotelDesc">Bold interior design marries stark white with bright graphics.Luxurious amenities in all unit types, including Pure spa products and plush bath towels.Beds are comfortable, and rooms are spotless. </p>
              </div>
              <div className="hotelDetailsPrice">
                  <h1>Perfect Stay</h1>
                    <span>Located in the heart of the city this hotel has a rating of 8.9!</span>
                    <h2><b>$550 </b>(3 nights)</h2>
                    <button>Book now!</button>
              </div>
            </div>
          </div>
        </div>
        <MailList/>
        <Footer/>
    </div>
  )
}

export default Hotel
